INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('La vida es bella', 'Jose Juan', '34', 10, 'Nenuco', '2000-12-01', 'Inglés', 'Francés', 100, '"La vida es bella" es una película italiana dirigida por Roberto Benigni y estrenada en 1997. La historia se desarrolla en la Italia fascista durante la Segunda Guerra Mundial y cuenta la historia de Guido, un hombre judío que se enamora de una mujer no judía llamada Dora. Juntos tienen un hijo llamado Giosué y la familia es deportada a un campo de concentración nazi.', 3, 'libro', 'Drama', 2, 'https://www.giglon.com/documents/10180/30003/3-20811x1-7f5pv.jpg/a37bd66d-7d43-4aae-a9d4-d1ce3dbe071c', null, null);
INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('Memorias del comunismo', 'Federico Jimenez Losantos', '9788491641780', 16, 'Planeta de los libros', '2003-09-14', 'Español', 'Español', 277, 'Buceando en las fuentes originales -de Marx, Bakunin y Lenin al Che o Pablo Iglesias-, este libro explica la naturaleza real del comunismo, sus raíces filosóficas y políticas, los errores habituales sobre su historia y el hecho más terrible: que, cien años y cien millones de muertos después, siga siendo una ideología respetada entre políticos, profesores y periodistas. En dos países europeos, Rusia y España, se intentó crear en el siglo XX un régimen comunista. En Rusia, tras cinco años de feroz guerra civil de Lenin contra su pueblo, el comunismo triunfó. En España, tras una atroz guerra civil de tres años, perdió. Pero se discute el papel de Stalin en la guerra -de Paracuellos y la muerte de Nin al oro del Banco de España- y se oculta la actuación de los dos comunismos españoles: el marxista del PCE, el PSOE bolchevizado o el POUM; y el bakuninista de la CNT-FAI, que impuso el terror rojo en Cataluña con la ayuda entusiasta de Companys. Ni la Rusia bajo la Cheka ni la España bajo las checas se recuerdan hoy. Solo eso y el éxito de la propaganda soviética desde 1917 explican la irrupción y el éxito de Podemos.', 36, 'libro', 'Política', 2, null, null, null);
INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('HISTORIA SECRETA DE MIS MEJORES CANCIONES', 'MIGUEL BOSE', '9788467066562', 12, 'HarperCollins', '2003-09-14', 'Español', 'Español', 123, '«Me temo que este libro va a romper muchas ilusiones. También historias arraigadas desde hace tiempo. Lo siento. No es mi intención ni la de este recopilatorio. Pero creo que, tras tantos años de licencia, llegó el momento de descifrar los secretos escondidos, esos que he venido guardando en cada una de estas 68 canciones. Entrad en el maravilloso mundo de mis laberintos, y desvelad lo que nadie sabe. Pero solo si sois valientes». A medida que Miguel Bosé nos desvela la intención de sus letras, irás encontrando fotografías a todo color inéditas del autor, las portadas de sus discos y códigos QR para escuchar las canciones. Sin duda, su obra más personal.', 1, 'libro', 'Biografía', 3, 'https://res.cloudinary.com/dnsxhjncj/image/upload/v1677689798/library/jfkron6n1dxs1nuhfh6x.jpg', null, null);
INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('Harry Potter y la piedra filosofal', 'J.K. Rowling', '978-8478886456', 13, 'Simon & Schuster', '2009-05-12', 'Francés', 'Español', 223, '"Harry Potter y la Piedra Filosofal" es el primer libro de la famosa saga escrita por J.K. Rowling. La historia sigue a Harry Potter, un niño huérfano que descubre que es un mago y es aceptado en la escuela de magia y hechicería de Hogwarts. Allí, Harry se enfrenta a misterios, amistades y desafíos mientras descubre su lugar en el mundo mágico. Junto a sus amigos Ron y Hermione, Harry debe encontrar la Piedra Filosofal, un objeto legendario con poderes inimaginables, antes de que caiga en manos equivocadas. Esta emocionante aventura lo lleva a descubrir su verdadero destino y enfrentarse al malvado Lord Voldemort.', 2, 'libro', 'Fantasía', 4, 'https://res.cloudinary.com/dnsxhjncj/image/upload/v1686139638/library/lv4ocnf4f0jmygcwn2vt.jpg', null, null);
INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('¿CÓMO NOS ARREGLAMOS?', 'ERNESTO CANALES SANTOS', '9786073172448', 7, 'Hachette Livre', '2000-12-02', 'Español', 'Inglés', 123, 'Ernesto Canales narra aquí cómo pasó, a sus 75 años, de ser un exitoso abogado a convertirse en el primer fiscal mexicano especializado en perseguir los delitos de corrupción, así como los retos que enfrentó durante los dos años que cumplió ese papel y los logros históricos que su trabajo legó en la administración pública del país. Al mismo tiempo, denuncia a los principales operadores criminales del sistema, desde las cúpulas políticas, y ofrece la relación detallada de sus procedimientos, develando los obstáculos que se han creado para sostener el sistema de impunidad y corrupción hasta la fecha. Porque su gestión no contó con el apoyo de la sociedad civil y, por encima de todo, no estuvo exenta de intimidaciones, trabas y la poca participación de los funcionarios, factores que impidieron constantemente su labor.', 3, null, 'Biografía', 0, 'https://res.cloudinary.com/dnsxhjncj/image/upload/v1686139969/library/u07du9bgfpenj3kuymdh.jpg', null, null);
INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('Cada historia cuenta', 'Varios autores', '9786073172448', 7, 'Pearson Education', '2003-09-14', 'Español', 'Inglés', 123, '“Cada historia cuenta” es un libro solidario de relatos que recoge las historias presentadas a la I edición del Concurso de Relatos “Cada historia cuenta” lanzado por la Fundación AINDACE. El lector o lectora encontrará entre sus páginas historias reales, vivencias y reflexiones personales sobre la discapacidad, desde una mirada positiva y enriquecedora, con historias  transcendentes de superación, capaces de empoderar, inspirar y motivar a otras personas. Se trata de un libro solidario donde parte de los beneficios obtenidos por su venta irán destinados a la Fundación AINDACE, cuya misión fundamental es ayudar a personas con daño cerebral a través de la financiación de proyectos de investigación.', 4, null, 'Historias', 0, 'https://res.cloudinary.com/dnsxhjncj/image/upload/v1686140332/library/jrtf6c64iqyydl75d26c.jpg', null, null);
INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('Filosofía felina ', 'John Gray', '9788418342530', 7, 'Scholastic Corporation', '2003-09-14', 'Inglés', 'Español', 168, 'Todos envidiamos el aristocrático desapego con el que los gatos contemplan el mundo y lo cómodos que parecen sentirse en su propio pellejo, como si supieran algo fundamental que nosotros ignoramos.	John Gray, uno de los grandes pensadores de nuestra época, defiende que tenemos mucho que aprender de los gatos y de su comportamiento, y partiendo de su experiencia personal con estos misteriosos y carismáticos animales, y acudiendo también a las brillantes páginas que les han dedicado autores como Montaigne, Junichiro Tanizaki o Patricia Highsmith, nos propone una suerte de filosofía felina que sirva de guía para una vida más auténtica y sosegada.	Las preguntas sobre el sentido de la existencia y el secreto de la felicidad nos persiguen desde los albores de la civilización, y quizá en esas lides, sugiere Gray, los gatos sean tan buenos maestros como los grandes filósofos.', 5, null, 'Ficción', 2, 'https://res.cloudinary.com/dnsxhjncj/image/upload/v1686140559/library/jt7urvryxwtdedntijs0.jpg', null, null);
INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('Heredera de fuego', ' SARAH J. MAAS', '9788418359309', 0, 'Oxford University Press', '2003-09-14', 'Inglés', 'Español', 672, 'Como asesina del rey, Celaena Sardothien está obligada a servir al tirano que asesinó a su mejor amiga. Pero se ha prometido a sí misma que se lo hará pagar. Las respuestas que Celaena necesita para destruir al rey se encuentran más allá del mar, en Wendlyn. Y Chaol, capitán de la guardia real, ha puesto su futuro en peligro al enviarla allí. Y mientras Celaena busca su destino en Wendlyn, una nueva amenaza se prepara para asaltar los cielos. ¿Encontrará Celaena las fuerzas necesarias no solo para ganar sus propias batallas, sino para ir a una guerra que podría poner a prueba la lealtad hacia los suyos y enfrentarla a aquellos que han llegado a convertirse en sus seres más queridos?', 23, null, 'Ficción', 2, 'https://res.cloudinary.com/dnsxhjncj/image/upload/v1686140666/library/cmbbi9c7x3dfxt8ifjqh.webp', null, null);
INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('El viento conoce mi nombre', 'ISABEL ALLENDE', '9788401032004', 16, 'Oxford University Press', '2002-02-17', 'Español', 'Francés', 352, 'Viena, 1938. Samuel Adler es un niño judío de seis años cuyo padre desaparece durante la Noche de los Cristales Rotos, en la que su familia lo pierde todo. Su madre, desesperada, le consigue una plaza en un tren que le llevará desde la Austria nazi hasta Inglaterra. Samuel emprende una nueva etapa con su fiel violín y con el peso de la soledad y la incertidumbre, que lo acompañarán siempre en su dilatada vida.', 7, null, 'Suspense', 2, 'https://res.cloudinary.com/dnsxhjncj/image/upload/v1686140859/library/tfijzfiteh1fayopjon6.jpg', null, null);
INSERT INTO libro (titulo, autores, isbn, edad, editorial, fechaEdicion, lenguaPublicacion, lenguaTraduccion, numeroPaginas, descripcion, edicion, formato, genero, copias, portada, imagen2, imagen3) VALUES ('Las brujas de Brooklyn', 'SOPHIE ESCABASSE', '9788468353708', 3, 'Cambridge University Press', '2008-11-14', 'Inglés', 'Español', 240, '¡Hay una nueva bruja en la ciudad!  Effie tiene nuevo hogar, nueva escuela y nuevos amigos (aunque resulte difícil al principio); pero su nueva vida en Brooklyn se vuelve aún más extraña cuando Effie descubre que la MAGIA le viene de familia...  Las brujas de Brooklyn es el primer libro de la trilogía. El cómic lo ha escrito y dibujado Sophie Escabasse, ilustradora francesa que vive en Nueva York. Esta es su primera novela gráfica aunque ha publicado otros libros como ilustradora', 1, null, 'Literatura infantil', 2, 'https://res.cloudinary.com/dnsxhjncj/image/upload/v1686140976/library/kpev4ixzs0vyllglyqpb.jpg', null, null);
